function setHours(){hoursBox.querySelector(".box-up").innerText=hours+1,hoursBox.querySelector(".box-top").innerText=hours}function setMinutes(){minutesBox.querySelector(".box-up").innerText=minutes+1,minutesBox.querySelector(".box-top").innerText=minutes}function setSeconds(){secondsBox.querySelector(".box-top").innerText=seconds+1,secondsBox.querySelector(".box-up").innerText=seconds}function changeHours(){hoursBox.querySelector(".box-up").classList.add("animate"),hoursBox.querySelector(".box-up").innerText=hours+1,hoursBox.querySelector(".box-top").innerText=hours}function changeMinutes(){minutesBox.querySelector(".box-up").classList.add("animate"),minutesBox.querySelector(".box-up").innerText=minutes+1,minutesBox.querySelector(".box-top").innerText=minutes}function changeSeconds(){secondsBox.querySelector(".box-up").classList.add("animate"),secondsBox.querySelector(".box-top").innerText=seconds+1,secondsBox.querySelector(".box-up").innerText=seconds}let date=new Date,hours=date.getHours(),minutes=date.getMinutes(),seconds=date.getSeconds();const hoursBox=document.querySelector('[data-time="HOURS"]'),minutesBox=document.querySelector('[data-time="MINUTES"]'),secondsBox=document.querySelector('[data-time="SECONDS"]'),boxs_top=[...document.querySelectorAll(".box-up")],tic=document.querySelector("audio"),play=document.querySelector(".btn");setHours(),setSeconds(),setMinutes(),play.addEventListener("click",()=>{tic.paused?(tic.currentTime=0,tic.play(),play.classList.add("tick")):(tic.pause(),play.classList.remove("tick"))}),setInterval(()=>{date=new Date,hours!==date.getHours()&&(hours=date.getHours(),changeHours()),minutes!=date.getMinutes()&&(minutes=date.getMinutes(),changeMinutes()),seconds!=date.getSeconds()&&(seconds=date.getSeconds(),changeSeconds())},1e3),boxs_top.forEach(e=>{e.addEventListener("animationend",e=>{e.target.classList.remove("animate")})});